"use strict";angular.module("accountApp",[]).controller("accountCtrl",["$scope","$http",function($scope,$http){$scope.data=null;$http.get("/userinfo").success(function(data){$scope.data=data.info}).error(function(data,status){console.log("error:");console.log(data);console.log(status)});$scope.getRegistrationDate=function(){if(!$scope.data){return""}return moment($scope.data.info.registrationDate).format("DD.MM.YYYY H:mm")};$scope.getAvatarUrl=function(index){if(!$scope.data){return""}switch(index){case 0:if(!$scope.data.personalData.avatar){return $scope.data.info.generatedAvatar}return $scope.data.personalData.avatar;case 1:if(!$scope.data.info.facebook||!$scope.data.info.facebook.photos){return $scope.data.info.generatedAvatar}return $scope.data.info.facebook.photos[0].value;case 2:if(!$scope.data.info.google||!$scope.data.info.google.photos){return $scope.data.info.generatedAvatar}return $scope.data.info.google.photos[0].value}};$scope.isFacebookActive=function(){if(!$scope.data){return false}return $scope.data.login.facebookProfileId};$scope.isGoogleActive=function(){if(!$scope.data){return false}return $scope.data.login.googleProfileId}}]);